            <input type="text" class="form-control" [ngClass]="{'is-valid' : oForm.controls.pass.dirty && !oForm.controls.pass.errors?.['required'] &&
              oForm.controls.pass.dirty && !oForm.controls.pass.errors?.['minlength']
                && oForm.controls.pass.dirty && !oForm.controls.pass.errors?.['maxlength'],
